-- View the UserName Password and Email of all user
SELECT [UserName], [Password], [Email], [City] FROM [Users];

-- The duplicate value does not exist in this table, The DISTINC statement return only distinct (different) values
SELECT DISTINCT [UserName] FROM [Users];

-- Get the number of users that have announcements
SELECT COUNT (DISTINCT [UserID]) FROM [CarsAnnoucements];

-- Get the car announcements order ascending by the price
SELECT [Title], [Price] FROM [CarsAnnoucements] ORDER BY [Price] ASC;

-- Get the car announcements order descending by the price where the announce is active
SELECT [Title], [Price], [Currency] FROM [CarsAnnoucements]  WHERE [Active] = 'YES' ORDER BY [Price] DESC;

-- Get the car announcements order ascending by the price where the announce is active and the prise is 25000 max
SELECT [Title], [Price], [Currency] FROM [CarsAnnoucements]  WHERE [Active] = 'YES' AND [Price] <= 25000  ORDER BY [Price] ASC;

-- Get the car announcements where Amg is in the title
SELECT [Title], [Brand], [Model], [Type], [Kilometer], [Price] FROM [CarsAnnoucements] WHERE [Title] LIKE '%AMG%';

-- Get the users that have an announcement
SELECT u.[UserName], u.[City], a.[UserID], a.[Title]
FROM [Users] u
	INNER JOIN [CarsAnnoucements] a ON a.[UserID]= u.[UserID];

-- Get all the users that have an announcement or not, the Left Join receive all the data in the left table
SELECT u.[UserName], u.[City], a.[UserID], a.[Title]
FROM [Users] u
	LEFT JOIN [CarsAnnoucements] a ON a.[UserID]= u.[UserID];

-- Get the number of Announcements group by the city
SELECT u.[City], COUNT(a.[UserID]) AS NumarAnunturi
FROM [Users] u
	INNER JOIN [CarsAnnoucements] a ON a.[UserID]= u.[UserID]
GROUP BY u.[City];

-- Get the number of Announcements group by the city bigger than 2
SELECT u.[City], COUNT(a.[UserID]) AS NumarAnunturi
FROM [Users] u
	INNER JOIN [CarsAnnoucements] a ON a.[UserID]= u.[UserID]
GROUP BY u.[City]
HAVING COUNT(u.[City]) > 2;

--Get all the users that have an announcement or not, the Right Join receive all the data in the right table but we don't have announcements without a user
SELECT u.[UserName], u.[City], a.[UserID], a.[Title]
FROM [Users] u
	RIGHT JOIN [CarsAnnoucements] a ON a.[UserID]= u.[UserID];

--Get all the data in the Joined tables
SELECT u.[UserName], u.[City], a.[UserID], a.[Title]
FROM [Users] u
	FULL OUTER JOIN [CarsAnnoucements] a ON a.[UserID]= u.[UserID]
ORDER BY a.[Price] DESC;

--Get the top 3 announcements order by price
SELECT TOP 3 u.[UserName], u.[City], a.[UserID], a.[Title], a.Price
FROM [Users] u
	FULL OUTER JOIN [CarsAnnoucements] a ON a.[UserID]= u.[UserID]
ORDER BY a.[Price] DESC;

